import 'package:remont_kz/data/api_builder.dart';import 'package:remont_kz/di.dart';import 'package:remont_kz/domain/repositories/auth_repo.dart';import 'package:remont_kz/domain/services/token_store_service.dart';class AuthRepoImpl extends AuthRepo {  final _api = getIt.get<ApiBuilder>().getAuthApi();  @override  Future<void> sendSms(String phone) async {    ///safeApiCall қалай қолданғанын көру    await _api.sendSms({'phone': phone});    // await safeApiCallVoid();  }  @override  Future<bool> checkSms(String username, int code, String password) async {    // final tokenModel = await safeApiCall<TokenModel>(_api.checkSms({'phone': phone, 'sms_code': code}));    final checkSms = await _api.checkSms({'username': username, 'code': code});    final tokenModel = await _api.login({'login': username, 'password': password});    await getIt<TokenStoreService>().saveToken(tokenModel);    return true;  }}