import 'dart:io';import 'package:remont_kz/data/api_builder.dart';import 'package:remont_kz/di.dart';import 'package:remont_kz/domain/entities/dic_response.dart';import 'package:remont_kz/domain/entities/upload_image.dart';import 'package:remont_kz/domain/entities/user.dart';import 'package:remont_kz/domain/repositories/profile_repo.dart';class ProfileRepoImpl extends ProfileRepo {  final _api = getIt.get<ApiBuilder>().getAuthApi();  final _fileApi = getIt.get<ApiBuilder>().getFileApi();  @override  Future<User?> getUser() async {    return await _api.getUser();  }  @override  Future<void> updateUser(User? user) async {    await _api.updateUser(user!.toJson());  }  @override  Future<UploadImage?> saveImage(File? file, String dir) async {    var response = await _fileApi.uploadImage(file!, dir);    return response;  }  @override  Future<void> updateAbout(String about) async {    await _api.updateUser({'about_me': about});  }  @override  Future<void> updateTermsAgree() async {    await _api.updateUser({'terms_agree': true});  }  @override  Future<DicRequest> getDics() async {    final response = await _api.getDics();    return response;  }  @override  Future<void> updateBirthDate(String birthDate) async {    await _api.updateUser({'birth_date': birthDate});  }  @override  Future<void> updateCity(int cityId) async {    await _api.updateUser({'city_id': cityId});  }  @override  Future<void> updateGender(int genderId) async {    await _api.updateUser({'gender': genderId});  }  @override  Future<void> updateName(String name) async {    await _api.updateUser({'name': name});  }  @override  Future<void> updateFamilyStatus(int statusId) async {    await _api.updateUser({'family_status': statusId});  }  @override  Future<void> updateHeiWei(int height, int weight) async {    await _api.updateUser({'height': height, 'weight': weight});  }  @override  Future<void> updateHobbies(List<String> hobbies) async {    await _api.updateUser({'hobbies': hobbies});  }  @override  Future<void> updateMarriagePeriod(String periodId) async {    await _api.updateUser({'marriage_period': periodId});  }  @override  Future<void> updateImage(List<String> images) async{    await _api.updateUser({'photos': images});  }}