import 'package:bottom_nav_layout/bottom_nav_layout.dart';import 'package:flutter/material.dart';import 'package:remont_kz/screens/category/category_screen.dart';import 'package:remont_kz/screens/create/create_screen.dart';import 'package:remont_kz/screens/main_screen/main_view_screen.dart';import 'package:remont_kz/screens/my_request/my_request_screen.dart';import 'package:remont_kz/screens/profile/my_profile_screen.dart';import 'package:remont_kz/utils/app_colors.dart';class MainNavbar extends StatelessWidget {  const MainNavbar({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return BottomNavLayout(      pages: [        (_) => const MainScreen(),        (_) => const CategoriesScreen(),        (_) => const CreateOrderScreen(),        (_) => const MyRequestScreen(),        (_) => const MyProfileScreen(),      ],      bottomNavigationBar: (currentIndex, onTap) => BottomNavigationBar(        currentIndex: currentIndex,        onTap: (index) => onTap(index),        items: const [          BottomNavigationBarItem(            icon: ImageIcon(AssetImage('assets/images/main.png')),            label: 'Главная',            activeIcon: ImageIcon(              AssetImage('assets/images/main.png'),              color: AppColors.primary,            ),          ),          BottomNavigationBarItem(            icon: ImageIcon(AssetImage('assets/images/category.png')),            label: 'Категории',            activeIcon: ImageIcon(              AssetImage('assets/images/category.png'),              color: AppColors.primary,            ),          ),          BottomNavigationBarItem(            icon: ImageIcon(AssetImage('assets/images/create.png')),            label: 'Создать',            activeIcon: ImageIcon(              AssetImage('assets/images/create.png'),              color: AppColors.primary,            ),          ),          BottomNavigationBarItem(            icon: ImageIcon(AssetImage('assets/images/my_order.png')),            label: 'Мои заявки',            activeIcon: ImageIcon(              AssetImage('assets/images/my_order.png'),              color: AppColors.primary,            ),          ),          BottomNavigationBarItem(            icon: ImageIcon(AssetImage('assets/images/profile.png')),            label: 'Профиль',            activeIcon: ImageIcon(              AssetImage('assets/images/profile.png'),              color: AppColors.primary,            ),          ),        ],        showSelectedLabels: true,        showUnselectedLabels: true,        selectedItemColor: AppColors.primary,        unselectedItemColor: AppColors.darkGray,        backgroundColor: AppColors.white,        selectedFontSize: 10,        elevation: 10,      ),    );  }}