import 'package:flutter/material.dart';import 'package:intl/intl.dart';final _phoneRegExp = RegExp(r'\+(\d{1,11})');extension AppStringExtension on String {  Uri get toUri => Uri.parse(this);  String get formattedPhone {    String source = this;    if (!source.startsWith('+')) {      source = '+$source';    }    final match = _phoneRegExp.firstMatch(source);    if (match == null) {      return this;    }    final digits = match[0]!.split('');    String formatted = '';    for (int i = 0; i < digits.length; i++) {      if (i == 2) {        formatted += ' (';      } else if (i == 5) {        formatted += ') ';      } else if (i == 8 || i == 10) {        formatted += '-';      }      formatted += digits[i];    }    return formatted;  }}extension AppIntExtension on int {  String get formattedCurrency => '${NumberFormat.simpleCurrency(locale: 'ru').format(this).split(',').first} â‚¸';}extension AppThemeExtension on BuildContext {  bool get isAndroid => Theme.of(this).platform == TargetPlatform.android;  bool get isIos => Theme.of(this).platform == TargetPlatform.iOS;}