import 'dart:io';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';class LoaderWidget extends StatelessWidget {  const LoaderWidget({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Platform.isAndroid ? SizedBox(      height: 24.h,        width: 24.w,        child: const CircularProgressIndicator()) : const CupertinoActivityIndicator();  }}