import 'package:flutter/material.dart';import 'package:remont_kz/generated/l10n.dart';class AppSettingsModel extends ChangeNotifier {  Locale locale = const Locale('ru', 'RU');  AppSettingsModel();  Future<void> setLang(String code) async {    if (!S.delegate.isSupported(Locale(code))) return;    print("code: $code");    locale = Locale(code);    await S.delegate.load(locale);    notifyListeners();    //для глобального установки языка    // await getIt.get<TokenStoreService>().setLang(locale);  }}